import React, { useState } from 'react';
import { Container, CssBaseline, ThemeProvider, createTheme, Box, Typography, AppBar, Toolbar, Tabs, Tab } from '@mui/material';
import { Provider } from 'react-redux';
import { store } from './store';
import AddTask from './components/AddTask';
import TaskList from './components/TaskList';
import AddCategory from './components/AddCategory';
import CategoryManager from './components/CategoryManager';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
    background: {
      default: '#f1f5f9',
    },
  },
  components: {
    MuiCard: {
      styleOverrides: {
        root: {
          borderRadius: 12,
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          borderRadius: 12,
        },
      },
    },
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 8,
          textTransform: 'none',
        },
      },
    },
  },
});

const APP_NAME = 'TaskManager';
const APP_VERSION = 'v1.0.0';

const App: React.FC = () => {
  const [page, setPage] = useState<'task' | 'category'>('task');

  return (
    <Provider store={store}>
      <ThemeProvider theme={theme}>
        <CssBaseline />
        <Box sx={{
          minHeight: '100vh',
          background: 'linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%)',
        }}>
          <AppBar position="static" elevation={0} sx={{
            background: 'rgba(255, 255, 255, 0.95)',
            backdropFilter: 'blur(10px)',
            borderBottom: '1px solid rgba(0, 0, 0, 0.08)',
          }}>
            <Toolbar sx={{ justifyContent: 'space-between', px: 3 }}>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                <Typography variant="h5" color="#2d3748" fontWeight={800} sx={{ letterSpacing: 1 }}>
                  {APP_NAME}
                </Typography>
                <Typography variant="caption" color="#64748b" fontWeight={600} sx={{ mt: 0.5 }}>
                  {APP_VERSION}
                </Typography>
              </Box>
              <Tabs
                value={page}
                onChange={(_, value) => setPage(value)}
                textColor="primary"
                indicatorColor="primary"
                sx={{
                  '& .MuiTab-root': {
                    fontWeight: 700,
                    fontSize: '1rem',
                    textTransform: 'none',
                    minWidth: 100,
                  },
                }}
              >
                <Tab label="Task" value="task" />
                <Tab label="Category" value="category" />
              </Tabs>
            </Toolbar>
          </AppBar>
          <Container maxWidth="lg" sx={{ py: 6 }}>
            {page === 'task' ? (
              <Box>
                <AddTask />
                <TaskList />
              </Box>
            ) : (
              <Box sx={{ maxWidth: 600, mx: 'auto' }}>
                <AddCategory />
                <CategoryManager />
              </Box>
            )}
          </Container>
          <Box component="footer" sx={{
            textAlign: 'center',
            py: 4,
            mt: 'auto',
            background: 'rgba(255, 255, 255, 0.8)',
            backdropFilter: 'blur(10px)',
            borderTop: '1px solid rgba(0, 0, 0, 0.08)',
          }}>
            <Typography variant="body2" color="#64748b" fontWeight={500}>
              Â© 2024 Ryuto Kobayashi
            </Typography>
          </Box>
        </Box>
      </ThemeProvider>
    </Provider>
  );
};

export default App;
