import React, { useState } from 'react';
import { Box, Typography, Paper, Divider, Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField } from '@mui/material';
import { useAppDispatch, useAppSelector } from '../hooks/redux';
import TaskItem from './TaskItem';
import { Priority } from '../types/task';
import { updateCategory, deleteCategory } from '../store/categorySlice';
import { deleteTask } from '../store/taskSlice';
import { Edit, Delete } from '@mui/icons-material';
import { IconButton } from '@mui/material';
import ColorPicker from './ColorPicker';
import ColorWarningDialog from './ColorWarningDialog';
import { findSimilarColors } from '../utils/colorUtils';
import { Category } from '../types/category';

const TaskList: React.FC = () => {
  const dispatch = useAppDispatch();
  const tasks = useAppSelector((state) => state.tasks.tasks);
  const categories = useAppSelector((state) => state.categories.categories);

  // デバッグ用：categoriesの状態をログ出力
  console.log('TaskList render - categories:', categories);
  const [editingCategory, setEditingCategory] = useState<Category | null>(null);
  const [editName, setEditName] = useState('');
  const [editColor, setEditColor] = useState('');
  const [showWarning, setShowWarning] = useState(false);
  const [pendingColor, setPendingColor] = useState('');

  const handleEdit = (category: Category) => {
    setEditingCategory(category);
    setEditName(category.name);
    setEditColor(category.color);
  };

  const handleColorChange = (newColor: string) => {
    setEditColor(newColor);
  };

  const handleWarningConfirm = () => {
    if (editingCategory && editName.trim()) {
      dispatch(updateCategory({
        id: editingCategory.id,
        updates: {
          name: editName.trim(),
          color: pendingColor,
        },
      }));
      setEditingCategory(null);
      setEditName('');
      setEditColor('');
    }
    setShowWarning(false);
  };

  const handleWarningCancel = () => {
    setShowWarning(false);
  };

  const handleSave = () => {
    if (editingCategory && editName.trim()) {
      const existingColors = categories
        .filter(cat => cat.id !== editingCategory.id)
        .map(cat => cat.color);
      const similarColors = findSimilarColors(editColor, existingColors);

      if (similarColors.length > 0) {
        setPendingColor(editColor);
        setShowWarning(true);
      } else {
        dispatch(updateCategory({
          id: editingCategory.id,
          updates: {
            name: editName.trim(),
            color: editColor,
          },
        }));
        setEditingCategory(null);
        setEditName('');
        setEditColor('');
      }
    }
  };

  const handleCancel = () => {
    setEditingCategory(null);
    setEditName('');
    setEditColor('');
  };

  const handleDelete = (categoryId: string) => {
    if (window.confirm('この分類を削除しますか？関連するタスクも削除されます。')) {
      // 関連するタスクを削除
      tasks.forEach(task => {
        if (task.categoryId === categoryId) {
          dispatch(deleteTask(task.id));
        }
      });
      // 分類を削除
      dispatch(deleteCategory(categoryId));
    }
  };

  // 優先度の順序を定義（数値が大きいほど優先度が高い）
  const priorityOrder: Record<Priority, number> = {
    urgent: 4,
    high: 3,
    medium: 2,
    low: 1,
  };

  // タスクをソートする関数
  const sortTasks = (taskList: typeof tasks) => {
    return [...taskList].sort((a, b) => {
      // 1. 優先度でソート（降順）
      const priorityDiff = priorityOrder[b.priority] - priorityOrder[a.priority];
      if (priorityDiff !== 0) return priorityDiff;

      // 2. 作成日でソート（降順）
      const dateDiff = new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime();
      if (dateDiff !== 0) return dateDiff;

      // 3. タスク名でソート（昇順）
      return a.title.localeCompare(b.title);
    });
  };

  if (tasks.length === 0) {
    return (
      <Paper elevation={1} sx={{ p: 4, textAlign: 'center', border: '1px solid', borderColor: 'divider' }}>
        <Typography variant="h6" color="text.secondary" sx={{ mb: 1 }}>
          タスクがありません
        </Typography>
        <Typography variant="body2" color="text.secondary">
          新しいタスクを追加してください
        </Typography>
      </Paper>
    );
  }

  return (
    <Box sx={{ display: 'flex', gap: 6, overflowX: 'auto', pb: 1 }}>
      {categories.map((category) => {
        const categoryTasks = sortTasks(tasks.filter(task => task.categoryId === category.id));
        if (categoryTasks.length === 0) return null;
        const total = categoryTasks.length;
        const completed = categoryTasks.filter(task => task.completed).length;
        const incompleteTasks = categoryTasks.filter(task => !task.completed);
        const completedTasks = categoryTasks.filter(task => task.completed);
        return (
          <Box key={category.id} sx={{ width: 510, minWidth: 510, maxWidth: 510, flex: '0 0 auto' }}>
            <Box sx={{ display: 'flex', alignItems: 'flex-start', justifyContent: 'space-between', mb: 2 }}>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                <Box sx={{ width: 16, height: 16, backgroundColor: category.color, borderRadius: '50%' }} />
                <Box>
                  <Typography variant="h6" fontWeight={600}>{category.name}</Typography>
                  <Box sx={{ display: 'flex', gap: 2, alignItems: 'center', mt: 0.5 }}>
                    <Typography variant="body2" color="text.secondary">
                      割当:{total}
                    </Typography>
                    <Typography variant="body2" color="success.main">
                      完了:{completed}
                    </Typography>
                  </Box>
                </Box>
              </Box>
              <Box sx={{ display: 'flex', gap: 0.5 }}>
                <IconButton
                  size="small"
                  onClick={() => handleEdit(category)}
                  sx={{
                    p: 0.5,
                    borderRadius: 2,
                    transition: 'background 0.2s',
                    '&:hover': {
                      background: 'rgba(25, 118, 210, 0.08)',
                      color: '#1976d2',
                    },
                  }}
                >
                  <Edit fontSize="small" />
                </IconButton>
                <IconButton
                  size="small"
                  color="error"
                  onClick={() => handleDelete(category.id)}
                  sx={{
                    p: 0.5,
                    borderRadius: 2,
                    transition: 'background 0.2s',
                    '&:hover': {
                      background: 'rgba(244, 67, 54, 0.08)',
                      color: '#d32f2f',
                    },
                  }}
                >
                  <Delete fontSize="small" />
                </IconButton>
              </Box>
            </Box>
            {/* 未完了タスク */}
            {incompleteTasks.length > 0 && (
              <Box sx={{ mb: completedTasks.length > 0 ? 2 : 0, width: '100%' }}>
                <Typography variant="subtitle2" color="text.secondary" sx={{ fontWeight: 600, mb: 1 }}>
                  未完了
                </Typography>
                {incompleteTasks.map((task) => (
                  <TaskItem key={task.id} task={task} />
                ))}
              </Box>
            )}
            {/* 完了タスク */}
            {completedTasks.length > 0 && (
              <Box sx={{ width: '100%' }}>
                <Typography variant="subtitle2" color="success.main" sx={{ fontWeight: 600, mb: 1 }}>
                  完了
                </Typography>
                {completedTasks.map((task) => (
                  <TaskItem key={task.id} task={task} />
                ))}
              </Box>
            )}
          </Box>
        );
      })}
      {/* 分類なしタスク */}
      {tasks.some(task => !task.categoryId) && (
        <Box sx={{ width: 510, minWidth: 510, maxWidth: 510, flex: '0 0 auto' }}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 2 }}>
            <Box sx={{ width: 16, height: 16, backgroundColor: 'grey.400', borderRadius: '50%' }} />
            <Typography variant="h6" fontWeight={600}>分類なし</Typography>
            <Box sx={{ display: 'flex', gap: 2, alignItems: 'center', ml: 'auto' }}>
              <Typography variant="body2" color="text.secondary">
                割当:{sortTasks(tasks.filter(task => !task.categoryId)).length}
              </Typography>
              <Typography variant="body2" color="success.main">
                完了:{sortTasks(tasks.filter(task => !task.categoryId && task.completed)).length}
              </Typography>
            </Box>
          </Box>
          {/* 分類なし未完了タスク */}
          {sortTasks(tasks.filter(task => !task.categoryId && !task.completed)).length > 0 && (
            <Box sx={{ mb: sortTasks(tasks.filter(task => !task.categoryId && task.completed)).length > 0 ? 2 : 0, width: '100%' }}>
              <Typography variant="subtitle2" color="text.secondary" sx={{ fontWeight: 600, mb: 1 }}>
                未完了
              </Typography>
              {sortTasks(tasks.filter(task => !task.categoryId && !task.completed)).map((task) => (
                <TaskItem key={task.id} task={task} />
              ))}
            </Box>
          )}
          {/* 分類なし完了タスク */}
          {sortTasks(tasks.filter(task => !task.categoryId && task.completed)).length > 0 && (
            <Box sx={{ width: '100%' }}>
              <Typography variant="subtitle2" color="success.main" sx={{ fontWeight: 600, mb: 1 }}>
                完了
              </Typography>
              {sortTasks(tasks.filter(task => !task.categoryId && task.completed)).map((task) => (
                <TaskItem key={task.id} task={task} />
              ))}
            </Box>
          )}
        </Box>
      )}
      {/* 分類編集ダイアログ */}
      <Dialog open={!!editingCategory} onClose={handleCancel} maxWidth="sm" fullWidth>
        <DialogTitle>分類を編集</DialogTitle>
        <DialogContent>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, mt: 1 }}>
            <TextField
              label="分類名"
              value={editName}
              onChange={(e) => setEditName(e.target.value)}
              fullWidth
              required
            />
            <ColorPicker
              color={editColor}
              onChange={handleColorChange}
              label="色を選択"
            />
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCancel}>キャンセル</Button>
          <Button onClick={handleSave} variant="contained" disabled={!editName.trim()}>
            保存
          </Button>
        </DialogActions>
      </Dialog>
      <ColorWarningDialog
        open={showWarning}
        onClose={handleWarningCancel}
        onConfirm={handleWarningConfirm}
        newColor={pendingColor}
        similarColors={findSimilarColors(
          pendingColor, 
          categories
            .filter(cat => cat.id !== editingCategory?.id)
            .map(cat => cat.color)
        )}
      />
    </Box>
  );
};

export default TaskList;
