import React, { useState } from 'react';
import { Container, CssBaseline, ThemeProvider, createTheme, Box, Typography, AppBar, Toolbar, Tabs, Tab } from '@mui/material';
import { Provider } from 'react-redux';
import { store } from './store';
import AddTask from './components/AddTask';
import TaskList from './components/TaskList';
import AddCategory from './components/AddCategory';
import CategoryManager from './components/CategoryManager';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
    background: {
      default: '#f8f9fa',
    },
  },
  components: {
    MuiCard: {
      styleOverrides: {
        root: {
          borderRadius: 8,
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          borderRadius: 8,
        },
      },
    },
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 6,
          textTransform: 'none',
        },
      },
    },
  },
});

const APP_NAME = 'TaskManager';
const APP_VERSION = 'v1.0.0';

const App: React.FC = () => {
  const [page, setPage] = useState<'task' | 'category'>('task');

  return (
    <Provider store={store}>
      <ThemeProvider theme={theme}>
        <CssBaseline />
        <AppBar position="static" color="default" elevation={1}>
          <Toolbar sx={{ justifyContent: 'space-between' }}>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
              <Typography variant="h6" color="primary" fontWeight={700}>
                {APP_NAME}
              </Typography>
              <Typography variant="caption" color="text.secondary" sx={{ mt: 0.5 }}>
                {APP_VERSION}
              </Typography>
            </Box>
            <Tabs
              value={page}
              onChange={(_, value) => setPage(value)}
              textColor="primary"
              indicatorColor="primary"
            >
              <Tab label="Task" value="task" />
              <Tab label="Category" value="category" />
            </Tabs>
          </Toolbar>
        </AppBar>
        <Container maxWidth="lg" sx={{ py: 4 }}>
          {page === 'task' ? (
            <Box>
              <AddTask />
              <TaskList />
            </Box>
          ) : (
            <Box sx={{ maxWidth: 600, mx: 'auto' }}>
              <AddCategory />
              <CategoryManager />
            </Box>
          )}
        </Container>
        <Box component="footer" sx={{ textAlign: 'center', py: 2, color: 'text.secondary', fontSize: 14 }}>
          Â© 2024 Ryuto Kobayashi
        </Box>
      </ThemeProvider>
    </Provider>
  );
};

export default App;
