import React, { useState } from 'react';
import {
  Box,
  TextField,
  Button,
  Paper,
  Typography,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from '@mui/material';
import { useAppDispatch } from '../hooks/redux';
import { addCategory } from '../store/categorySlice';

const colorOptions = [
  { value: '#1976d2', label: '青' },
  { value: '#dc004e', label: '赤' },
  { value: '#388e3c', label: '緑' },
  { value: '#f57c00', label: 'オレンジ' },
  { value: '#7b1fa2', label: '紫' },
  { value: '#d32f2f', label: '深紅' },
  { value: '#e91e63', label: 'ピンク' },
  { value: '#00bcd4', label: 'シアン' },
  { value: '#8bc34a', label: 'ライトグリーン' },
  { value: '#ffc107', label: 'アンバー' },
  { value: '#673ab7', label: 'ディープパープル' },
  { value: '#607d8b', label: 'ブルーグレー' },
  { value: '#795548', label: 'ブラウン' },
  { value: '#009688', label: 'ティール' },
  { value: '#3f51b5', label: 'インディゴ' },
];

const AddCategory: React.FC = () => {
  const dispatch = useAppDispatch();
  const [name, setName] = useState('');
  const [color, setColor] = useState('#1976d2');
  const [isOpen, setIsOpen] = useState(false);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (name.trim()) {
      dispatch(addCategory({
        name: name.trim(),
        color,
      }));
      setName('');
      setColor('#1976d2');
      setIsOpen(false);
    }
  };

  const handleCancel = () => {
    setName('');
    setColor('#1976d2');
    setIsOpen(false);
  };

  if (!isOpen) {
    return (
      <Button
        variant="outlined"
        onClick={() => setIsOpen(true)}
        sx={{ mb: 3, width: '100%' }}
      >
        新しい分類を追加
      </Button>
    );
  }

  return (
    <Paper elevation={2} sx={{ p: 2, mb: 3 }}>
      <Typography variant="h6" gutterBottom>
        新しい分類を追加
      </Typography>
      <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
        <TextField
          label="分類名"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          fullWidth
        />
        <FormControl fullWidth>
          <InputLabel>色</InputLabel>
          <Select
            value={color}
            label="色"
            onChange={(e) => setColor(e.target.value)}
          >
            {colorOptions.map((option) => (
              <MenuItem key={option.value} value={option.value}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <Box
                    sx={{
                      width: 20,
                      height: 20,
                      backgroundColor: option.value,
                      borderRadius: '50%',
                    }}
                  />
                  {option.label}
                </Box>
              </MenuItem>
            ))}
          </Select>
        </FormControl>
        <Box sx={{ display: 'flex', gap: 1 }}>
          <Button
            type="submit"
            variant="contained"
            disabled={!name.trim()}
          >
            分類を追加
          </Button>
          <Button
            variant="outlined"
            onClick={handleCancel}
          >
            キャンセル
          </Button>
        </Box>
      </Box>
    </Paper>
  );
};

export default AddCategory;
