import React from 'react';
import { Container, CssBaseline, ThemeProvider, createTheme, Box, Typography } from '@mui/material';
import { Provider } from 'react-redux';
import { store } from './store';
import AddTask from './components/AddTask';
import TaskList from './components/TaskList';
import AddCategory from './components/AddCategory';
import CategoryManager from './components/CategoryManager';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#dc004e',
    },
    background: {
      default: '#f8f9fa',
    },
  },
  components: {
    MuiCard: {
      styleOverrides: {
        root: {
          borderRadius: 8,
        },
      },
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          borderRadius: 8,
        },
      },
    },
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 6,
          textTransform: 'none',
        },
      },
    },
  },
});

const App: React.FC = () => {
  return (
    <Provider store={store}>
      <ThemeProvider theme={theme}>
        <CssBaseline />
        <Container maxWidth="lg" sx={{ py: 4 }}>
          <Box sx={{ 
            display: 'grid', 
            gridTemplateColumns: { xs: '1fr', md: '1fr 2fr' }, 
            gap: { xs: 3, md: 4 } 
          }}>
            {/* 左側: 分類管理 */}
            <Box>
              <Typography variant="h4" gutterBottom sx={{ mb: 3, display: { xs: 'none', md: 'block' } }}>
                タスクマネージャー
              </Typography>
              <Typography variant="h5" gutterBottom sx={{ mb: 3, display: { xs: 'block', md: 'none' } }}>
                タスクマネージャー
              </Typography>
              <AddCategory />
              <CategoryManager />
            </Box>

            {/* 右側: タスク管理 */}
            <Box>
              <AddTask />
              <TaskList />
            </Box>
          </Box>
        </Container>
      </ThemeProvider>
    </Provider>
  );
};

export default App;
