import React, { useState } from 'react';
import {
  Card,
  CardContent,
  Typography,
  Checkbox,
  IconButton,
  Box,
  TextField,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from '@mui/material';
import { Delete, Edit, Save, Cancel } from '@mui/icons-material';
import { useAppDispatch, useAppSelector } from '../hooks/redux';
import { updateTask, deleteTask, toggleTaskComplete } from '../store/taskSlice';
import { Task } from '../types/task';

interface TaskItemProps {
  task: Task;
}

const TaskItem: React.FC<TaskItemProps> = ({ task }) => {
  const dispatch = useAppDispatch();
  const categories = useAppSelector((state) => state.categories.categories);
  const [isEditing, setIsEditing] = useState(false);
  const [editTitle, setEditTitle] = useState(task.title);
  const [editDescription, setEditDescription] = useState(task.description);
  const [editCategoryId, setEditCategoryId] = useState(task.categoryId || '');

  const handleToggleComplete = () => {
    dispatch(toggleTaskComplete(task.id));
  };

  const handleDelete = () => {
    dispatch(deleteTask(task.id));
  };

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = () => {
    if (editTitle.trim()) {
      dispatch(updateTask({
        id: task.id,
        updates: {
          title: editTitle.trim(),
          description: editDescription.trim(),
          categoryId: editCategoryId || null,
        },
      }));
      setIsEditing(false);
    }
  };

  const handleCancel = () => {
    setEditTitle(task.title);
    setEditDescription(task.description);
    setEditCategoryId(task.categoryId || '');
    setIsEditing(false);
  };

  return (
    <Card 
      elevation={1} 
      sx={{ 
        mb: 2,
        opacity: task.completed ? 0.7 : 1,
        textDecoration: task.completed ? 'line-through' : 'none',
        border: '1px solid',
        borderColor: 'divider',
        minHeight: 80,
        maxHeight: 100,
        width: '100%',
        display: 'flex',
        alignItems: 'center',
      }}
    >
      <CardContent sx={{ p: 2, width: '100%', display: 'flex', alignItems: 'center' }}>
        {isEditing ? (
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            <TextField
              label="タスク名"
              value={editTitle}
              onChange={(e) => setEditTitle(e.target.value)}
              fullWidth
              required
            />
            <TextField
              label="説明"
              value={editDescription}
              onChange={(e) => setEditDescription(e.target.value)}
              multiline
              rows={2}
              fullWidth
            />
            <FormControl fullWidth>
              <InputLabel>分類</InputLabel>
              <Select
                value={editCategoryId}
                label="分類"
                onChange={(e) => setEditCategoryId(e.target.value)}
              >
                <MenuItem value="">
                  <em>分類なし</em>
                </MenuItem>
                {categories.map((category) => (
                  <MenuItem key={category.id} value={category.id}>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <Box
                        sx={{
                          width: 16,
                          height: 16,
                          backgroundColor: category.color,
                          borderRadius: '50%',
                        }}
                      />
                      {category.name}
                    </Box>
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
            <Box sx={{ display: 'flex', gap: 1 }}>
              <Button
                variant="contained"
                size="small"
                onClick={handleSave}
                disabled={!editTitle.trim()}
                startIcon={<Save />}
              >
                保存
              </Button>
              <Button
                variant="outlined"
                size="small"
                onClick={handleCancel}
                startIcon={<Cancel />}
              >
                キャンセル
              </Button>
            </Box>
          </Box>
        ) : (
          <Box sx={{ display: 'flex', alignItems: 'flex-start', gap: 2, width: '100%' }}>
            <Checkbox
              checked={task.completed}
              onChange={handleToggleComplete}
              color="primary"
            />
            <Box sx={{ flexGrow: 1, minWidth: 0 }}>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1, minWidth: 0 }}>
                <Typography variant="h6" component="div" sx={{
                  overflow: 'hidden',
                  textOverflow: 'ellipsis',
                  whiteSpace: 'nowrap',
                  maxWidth: '220px',
                }}>
                  {task.title}
                </Typography>
                {task.categoryId && (
                  (() => {
                    const category = categories.find(c => c.id === task.categoryId);
                    return category ? (
                      <Box
                        sx={{
                          px: 1,
                          py: 0.25,
                          backgroundColor: category.color,
                          color: 'white',
                          borderRadius: 0.5,
                          fontSize: '0.7rem',
                          fontWeight: 500,
                          maxWidth: '80px',
                          overflow: 'hidden',
                          textOverflow: 'ellipsis',
                          whiteSpace: 'nowrap',
                        }}
                      >
                        {category.name}
                      </Box>
                    ) : null;
                  })()
                )}
              </Box>
              {task.description && (
                <Typography variant="body2" color="text.secondary" sx={{
                  mt: 1,
                  overflow: 'hidden',
                  textOverflow: 'ellipsis',
                  whiteSpace: 'nowrap',
                  maxWidth: '260px',
                  display: 'block',
                }}>
                  {task.description}
                </Typography>
              )}
              <Typography variant="caption" color="text.secondary" sx={{ display: 'block', mt: 1 }}>
                作成日: {new Date(task.createdAt).toLocaleDateString('ja-JP')}
              </Typography>
            </Box>
            <Box sx={{ display: 'flex', gap: 1 }}>
              <IconButton onClick={handleEdit} size="small">
                <Edit />
              </IconButton>
              <IconButton onClick={handleDelete} size="small" color="error">
                <Delete />
              </IconButton>
            </Box>
          </Box>
        )}
      </CardContent>
    </Card>
  );
};

export default TaskItem;
