import React, { useState } from 'react';
import {
  Card,
  CardContent,
  Typography,
  Checkbox,
  IconButton,
  Box,
  TextField,
  Button,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from '@mui/material';
import { Delete, Edit, Save, Cancel } from '@mui/icons-material';
import { useAppDispatch, useAppSelector } from '../hooks/redux';
import { updateTask, deleteTask, toggleTaskComplete } from '../store/taskSlice';
import { Task } from '../types/task';

interface TaskItemProps {
  task: Task;
}

const TaskItem: React.FC<TaskItemProps> = ({ task }) => {
  const dispatch = useAppDispatch();
  const categories = useAppSelector((state) => state.categories.categories);
  const [isEditing, setIsEditing] = useState(false);
  const [editTitle, setEditTitle] = useState(task.title);
  const [editDescription, setEditDescription] = useState(task.description);
  const [editCategoryId, setEditCategoryId] = useState(task.categoryId || '');

  const handleToggleComplete = () => {
    dispatch(toggleTaskComplete(task.id));
  };

  const handleDelete = () => {
    dispatch(deleteTask(task.id));
  };

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = () => {
    if (editTitle.trim()) {
      dispatch(updateTask({
        id: task.id,
        updates: {
          title: editTitle.trim(),
          description: editDescription.trim(),
          categoryId: editCategoryId || null,
        },
      }));
      setIsEditing(false);
    }
  };

  const handleCancel = () => {
    setEditTitle(task.title);
    setEditDescription(task.description);
    setEditCategoryId(task.categoryId || '');
    setIsEditing(false);
  };

  return (
    <Card 
      elevation={3} 
      sx={{ 
        mb: 2,
        border: 'none',
        borderRadius: 3,
        boxShadow: '0 4px 16px 0 rgba(60,72,88,0.10)',
        background: '#f8fafc',
        transition: 'box-shadow 0.2s',
        '&:hover': {
          boxShadow: '0 8px 32px 0 rgba(60,72,88,0.18)',
        },
        width: '100%',
        display: 'flex',
        alignItems: 'stretch',
      }}
    >
      <CardContent sx={{ p: 3, width: '100%', flex: 1, display: 'block' }}>
        {isEditing ? (
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
            <TextField
              label="タスク名"
              value={editTitle}
              onChange={(e) => setEditTitle(e.target.value)}
              fullWidth
              required
            />
            <TextField
              label="説明"
              value={editDescription}
              onChange={(e) => setEditDescription(e.target.value)}
              multiline
              rows={2}
              fullWidth
            />
            <FormControl fullWidth>
              <InputLabel>分類</InputLabel>
              <Select
                value={editCategoryId}
                label="分類"
                onChange={(e) => setEditCategoryId(e.target.value)}
              >
                <MenuItem value="">
                  <em>分類なし</em>
                </MenuItem>
                {categories.map((category) => (
                  <MenuItem key={category.id} value={category.id}>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <Box
                        sx={{
                          width: 16,
                          height: 16,
                          backgroundColor: category.color,
                          borderRadius: '50%',
                        }}
                      />
                      {category.name}
                    </Box>
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
            <Box sx={{ display: 'flex', gap: 1 }}>
              <Button
                variant="contained"
                size="small"
                onClick={handleSave}
                disabled={!editTitle.trim()}
                startIcon={<Save />}
              >
                保存
              </Button>
              <Button
                variant="outlined"
                size="small"
                onClick={handleCancel}
                startIcon={<Cancel />}
              >
                キャンセル
              </Button>
            </Box>
          </Box>
        ) : (
          <Box sx={{ display: 'flex', alignItems: 'flex-start', gap: 2, width: '100%' }}>
            <Checkbox
              checked={task.completed}
              onChange={handleToggleComplete}
              color="primary"
              sx={{
                mt: 0.5,
                '&:hover': {
                  backgroundColor: 'rgba(25, 118, 210, 0.08)',
                  borderRadius: '50%',
                },
              }}
            />
            <Box sx={{ flexGrow: 1, minWidth: 0, display: 'flex', flexDirection: 'column', height: '100%' }}>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1, minWidth: 0 }}>
                <Typography variant="h6" component="div" sx={{
                  overflow: 'hidden',
                  textOverflow: 'ellipsis',
                  whiteSpace: 'nowrap',
                  width: '100%',
                  fontWeight: 700,
                  color: '#2d3748',
                  letterSpacing: 0.5,
                }}>
                  {task.title}
                </Typography>
                {task.categoryId && (
                  (() => {
                    const category = categories.find(c => c.id === task.categoryId);
                    return category ? (
                      <Box
                        sx={{
                          px: 1.5,
                          py: 0.25,
                          backgroundColor: category.color,
                          color: '#fff',
                          borderRadius: 1,
                          fontSize: '0.8rem',
                          fontWeight: 600,
                          ml: 1,
                        }}
                      >
                        {category.name}
                      </Box>
                    ) : null;
                  })()
                )}
              </Box>
              {task.description && (
                <Typography variant="body2" color="text.secondary" sx={{
                  mt: 1,
                  overflow: 'hidden',
                  textOverflow: 'ellipsis',
                  whiteSpace: 'nowrap',
                  width: '100%',
                  display: 'block',
                  fontWeight: 500,
                  color: '#4b5563',
                }}>
                  {task.description}
                </Typography>
              )}
              <Box sx={{ flexGrow: 1 }} />
              <Typography variant="caption" color="text.secondary" sx={{ display: 'block', mt: 'auto', overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap', color: '#64748b', fontWeight: 500 }}>
                作成日: {new Date(task.createdAt).toLocaleDateString('ja-JP')}
              </Typography>
            </Box>
            <Box sx={{ display: 'flex', gap: 1 }}>
              <IconButton onClick={handleEdit} size="small" sx={{
                transition: 'background 0.2s',
                '&:hover': {
                  background: 'rgba(25, 118, 210, 0.08)',
                  color: '#1976d2',
                },
              }}>
                <Edit />
              </IconButton>
              <IconButton onClick={handleDelete} size="small" color="error" sx={{
                transition: 'background 0.2s',
                '&:hover': {
                  background: 'rgba(244, 67, 54, 0.08)',
                  color: '#d32f2f',
                },
              }}>
                <Delete />
              </IconButton>
            </Box>
          </Box>
        )}
      </CardContent>
    </Card>
  );
};

export default TaskItem;
