import React, { useState } from 'react';
import {
  Box,
  TextField,
  Button,
  Paper,
  Typography,
} from '@mui/material';
import { useAppDispatch, useAppSelector } from '../hooks/redux';
import { addCategory } from '../store/categorySlice';
import ColorPicker from './ColorPicker';
import ColorWarningDialog from './ColorWarningDialog';
import { findSimilarColors } from '../utils/colorUtils';



const AddCategory: React.FC = () => {
  const dispatch = useAppDispatch();
  const categories = useAppSelector((state) => state.categories.categories);
  const [name, setName] = useState('');
  const [color, setColor] = useState('#1976d2');
  const [isOpen, setIsOpen] = useState(false);
  const [showWarning, setShowWarning] = useState(false);
  const [pendingColor, setPendingColor] = useState('#1976d2');

  const handleColorChange = (newColor: string) => {
    const existingColors = categories.map(cat => cat.color);
    const similarColors = findSimilarColors(newColor, existingColors);

    if (similarColors.length > 0) {
      setPendingColor(newColor);
      setShowWarning(true);
    } else {
      setColor(newColor);
    }
  };

  const handleWarningConfirm = () => {
    setColor(pendingColor);
    setShowWarning(false);
  };

  const handleWarningCancel = () => {
    setShowWarning(false);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (name.trim()) {
      dispatch(addCategory({
        name: name.trim(),
        color,
      }));
      setName('');
      setColor('#1976d2');
      setIsOpen(false);
    }
  };

  const handleCancel = () => {
    setName('');
    setColor('#1976d2');
    setIsOpen(false);
  };

  if (!isOpen) {
    return (
      <Button
        variant="outlined"
        onClick={() => setIsOpen(true)}
        sx={{ mb: 3, width: '100%' }}
      >
        新しい分類を追加
      </Button>
    );
  }

  return (
    <>
      <Paper elevation={2} sx={{ p: 2, mb: 3 }}>
        <Typography variant="h6" gutterBottom>
          新しい分類を追加
        </Typography>
        <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
          <TextField
            label="分類名"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            fullWidth
          />
          <ColorPicker
            color={color}
            onChange={handleColorChange}
            label="色を選択"
          />
          <Box sx={{ display: 'flex', gap: 1 }}>
            <Button
              type="submit"
              variant="contained"
              disabled={!name.trim()}
            >
              分類を追加
            </Button>
            <Button
              variant="outlined"
              onClick={handleCancel}
            >
              キャンセル
            </Button>
          </Box>
        </Box>
      </Paper>
      <ColorWarningDialog
        open={showWarning}
        onClose={handleWarningCancel}
        onConfirm={handleWarningConfirm}
        newColor={pendingColor}
        similarColors={findSimilarColors(pendingColor, categories.map(cat => cat.color))}
      />
    </>
  );
};

export default AddCategory;
