import React, { useState } from 'react';
import {
  Box,
  TextField,
  Button,
  Paper,
  Typography,
} from '@mui/material';
import { useAppDispatch, useAppSelector } from '../hooks/redux';
import { addCategory } from '../store/categorySlice';
import ColorPicker from './ColorPicker';
import ColorWarningDialog from './ColorWarningDialog';
import { findSimilarColors, getRandomColor } from '../utils/colorUtils';


const AddCategory: React.FC = () => {
  const dispatch = useAppDispatch();
  const categories = useAppSelector((state) => state.categories.categories);
  const [name, setName] = useState('');
  const [color, setColor] = useState(getRandomColor());
  const [showWarning, setShowWarning] = useState(false);
  const [pendingColor, setPendingColor] = useState(getRandomColor());

  const handleColorChange = (newColor: string) => {
    setColor(newColor);
  };

  const handleWarningConfirm = () => {
    dispatch(addCategory({
      name: name.trim(),
      color: pendingColor,
    }));
    setName('');
    setColor(getRandomColor());
    setShowWarning(false);
  };

  const handleWarningCancel = () => {
    setShowWarning(false);
    // 警告をキャンセルした場合は何もしない（フォームは開いたまま）
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (name.trim()) {
      const existingColors = categories.map(cat => cat.color);
      const similarColors = findSimilarColors(color, existingColors);

      console.log('Submit check:', { color, existingColors, similarColors });

      if (similarColors.length > 0) {
        setPendingColor(color);
        setShowWarning(true);
      } else {
        dispatch(addCategory({
          name: name.trim(),
          color,
        }));
        setName('');
        setColor(getRandomColor());
      }
    }
  };

  const handleCancel = () => {
    setName('');
    setColor(getRandomColor());
  };

  return (
    <>
      <Paper elevation={3} sx={{
        p: 4,
        mb: 4,
        borderRadius: 3,
        background: '#f8fafc',
        boxShadow: '0 4px 16px 0 rgba(60,72,88,0.10)',
        maxWidth: 520,
        mx: 'auto',
      }}>
        <Typography variant="h5" fontWeight={700} color="#2d3748" gutterBottom sx={{ mb: 3, letterSpacing: 1 }}>
          新しい分類を追加
        </Typography>
        <Box component="form" onSubmit={handleSubmit} sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
          <TextField
            label="分類名"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
            fullWidth
            variant="outlined"
            sx={{ background: '#fff', borderRadius: 2 }}
          />
          <ColorPicker
            color={color}
            onChange={handleColorChange}
            label="色を選択"
          />
          <Box sx={{ display: 'flex', gap: 2 }}>
            <Button
              type="submit"
              variant="contained"
              disabled={!name.trim()}
              sx={{
                borderRadius: 2,
                px: 4,
                py: 1.2,
                fontWeight: 700,
                fontSize: '1rem',
                boxShadow: '0 2px 8px 0 rgba(25,118,210,0.08)',
                textTransform: 'none',
                transition: 'background 0.2s',
                background: 'linear-gradient(90deg, #1976d2 60%, #1565c0 100%)',
                '&:hover': {
                  background: 'linear-gradient(90deg, #1565c0 60%, #1976d2 100%)',
                },
              }}
            >
              分類を追加
            </Button>
            <Button
              variant="outlined"
              onClick={handleCancel}
              sx={{
                borderRadius: 2,
                fontWeight: 700,
                fontSize: '1rem',
                py: 1.2,
                borderColor: '#1976d2',
                color: '#1976d2',
                textTransform: 'none',
                transition: 'background 0.2s',
                '&:hover': {
                  background: 'rgba(25,118,210,0.08)',
                  borderColor: '#1565c0',
                  color: '#1565c0',
                },
              }}
            >
              キャンセル
            </Button>
          </Box>
        </Box>
      </Paper>
      <ColorWarningDialog
        open={showWarning}
        onClose={handleWarningCancel}
        onConfirm={handleWarningConfirm}
        newColor={pendingColor}
        similarColors={findSimilarColors(pendingColor, categories.map(cat => cat.color))}
      />
    </>
  );
};

export default AddCategory;
