import React, { useState } from 'react';
import {
  Box,
  Card,
  CardContent,
  Typography,
  IconButton,
  Chip,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
} from '@mui/material';
import { Edit, Delete } from '@mui/icons-material';
import { useAppDispatch, useAppSelector } from '../hooks/redux';
import { updateCategory, deleteCategory } from '../store/categorySlice';
import { deleteTask } from '../store/taskSlice';
import { Category } from '../types/category';

const colorOptions = [
  { value: '#1976d2', label: '青' },
  { value: '#dc004e', label: '赤' },
  { value: '#388e3c', label: '緑' },
  { value: '#f57c00', label: 'オレンジ' },
  { value: '#7b1fa2', label: '紫' },
  { value: '#d32f2f', label: '深紅' },
];

const CategoryManager: React.FC = () => {
  const dispatch = useAppDispatch();
  const categories = useAppSelector((state) => state.categories.categories);
  const tasks = useAppSelector((state) => state.tasks.tasks);
  const [editingCategory, setEditingCategory] = useState<Category | null>(null);
  const [editName, setEditName] = useState('');
  const [editColor, setEditColor] = useState('');

  const handleEdit = (category: Category) => {
    setEditingCategory(category);
    setEditName(category.name);
    setEditColor(category.color);
  };

  const handleSave = () => {
    if (editingCategory && editName.trim()) {
      dispatch(updateCategory({
        id: editingCategory.id,
        updates: {
          name: editName.trim(),
          color: editColor,
        },
      }));
      setEditingCategory(null);
      setEditName('');
      setEditColor('');
    }
  };

  const handleCancel = () => {
    setEditingCategory(null);
    setEditName('');
    setEditColor('');
  };

  const handleDelete = (categoryId: string) => {
    if (window.confirm('この分類を削除しますか？関連するタスクも削除されます。')) {
      // 関連するタスクを削除
      tasks.forEach(task => {
        if (task.categoryId === categoryId) {
          dispatch(deleteTask(task.id));
        }
      });
      // 分類を削除
      dispatch(deleteCategory(categoryId));
    }
  };

  return (
    <Box>
      <Typography variant="h6" gutterBottom>
        分類管理
      </Typography>
      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2 }}>
        {categories.map((category) => (
          <Card key={category.id} sx={{ minWidth: 200 }}>
            <CardContent>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1 }}>
                <Box
                  sx={{
                    width: 16,
                    height: 16,
                    backgroundColor: category.color,
                    borderRadius: '50%',
                  }}
                />
                <Typography variant="h6" component="div">
                  {category.name}
                </Typography>
              </Box>
              <Box sx={{ display: 'flex', gap: 1 }}>
                <IconButton
                  size="small"
                  onClick={() => handleEdit(category)}
                >
                  <Edit />
                </IconButton>
                <IconButton
                  size="small"
                  color="error"
                  onClick={() => handleDelete(category.id)}
                >
                  <Delete />
                </IconButton>
              </Box>
            </CardContent>
          </Card>
        ))}
      </Box>

      <Dialog open={!!editingCategory} onClose={handleCancel} maxWidth="sm" fullWidth>
        <DialogTitle>分類を編集</DialogTitle>
        <DialogContent>
          <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2, pt: 1 }}>
            <TextField
              label="分類名"
              value={editName}
              onChange={(e) => setEditName(e.target.value)}
              fullWidth
              required
            />
            <FormControl fullWidth>
              <InputLabel>色</InputLabel>
              <Select
                value={editColor}
                label="色"
                onChange={(e) => setEditColor(e.target.value)}
              >
                {colorOptions.map((option) => (
                  <MenuItem key={option.value} value={option.value}>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <Box
                        sx={{
                          width: 20,
                          height: 20,
                          backgroundColor: option.value,
                          borderRadius: '50%',
                        }}
                      />
                      {option.label}
                    </Box>
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCancel}>キャンセル</Button>
          <Button onClick={handleSave} variant="contained" disabled={!editName.trim()}>
            保存
          </Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default CategoryManager;
