import React, { useState } from 'react';
import { Box, Typography, Paper, ToggleButtonGroup, ToggleButton, Divider } from '@mui/material';
import { useAppSelector } from '../hooks/redux';
import TaskItem from './TaskItem';

const TaskList: React.FC = () => {
  const tasks = useAppSelector((state) => state.tasks.tasks);
  const categories = useAppSelector((state) => state.categories.categories);
  const [viewMode, setViewMode] = useState<'all' | 'byCategory'>('all');

  if (tasks.length === 0) {
    return (
      <Paper elevation={1} sx={{ p: 4, textAlign: 'center', border: '1px solid', borderColor: 'divider' }}>
        <Typography variant="h6" color="text.secondary" sx={{ mb: 1 }}>
          タスクがありません
        </Typography>
        <Typography variant="body2" color="text.secondary">
          新しいタスクを追加してください
        </Typography>
      </Paper>
    );
  }

  return (
    <Box>
      <Box sx={{ display: 'flex', alignItems: 'center', mb: 2, gap: 2 }}>
        <Typography variant="h6" sx={{ flexGrow: 1 }}>
          タスク一覧
        </Typography>
        <ToggleButtonGroup
          value={viewMode}
          exclusive
          onChange={(_, value) => value && setViewMode(value)}
          size="small"
        >
          <ToggleButton value="all">すべて</ToggleButton>
          <ToggleButton value="byCategory">分類ごと</ToggleButton>
        </ToggleButtonGroup>
      </Box>
      {viewMode === 'all' ? (
        <>
          {tasks.map((task) => (
            <TaskItem key={task.id} task={task} />
          ))}
        </>
      ) : (
        <Box>
          {categories.map((category) => {
            const categoryTasks = tasks.filter(task => task.categoryId === category.id);
            if (categoryTasks.length === 0) return null;
            return (
              <Box key={category.id} sx={{ mb: 3 }}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1 }}>
                  <Box sx={{ width: 16, height: 16, backgroundColor: category.color, borderRadius: '50%' }} />
                  <Typography variant="subtitle1" fontWeight={600}>{category.name}</Typography>
                </Box>
                {categoryTasks.map((task) => (
                  <TaskItem key={task.id} task={task} />
                ))}
                <Divider sx={{ mt: 2 }} />
              </Box>
            );
          })}
          {/* 分類なしタスク */}
          {tasks.some(task => !task.categoryId) && (
            <Box sx={{ mb: 3 }}>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1 }}>
                <Box sx={{ width: 16, height: 16, backgroundColor: 'grey.400', borderRadius: '50%' }} />
                <Typography variant="subtitle1" fontWeight={600}>分類なし</Typography>
              </Box>
              {tasks.filter(task => !task.categoryId).map((task) => (
                <TaskItem key={task.id} task={task} />
              ))}
            </Box>
          )}
        </Box>
      )}
    </Box>
  );
};

export default TaskList;
